                                      </div>
                                      <div>
                                        <small className="text-warning">
                                          {" "}
                                          <i className="bi bi-star-fill" />
                                          <i className="bi bi-star-fill" />
                                          <i className="bi bi-star-fill" />
                                          <i className="bi bi-star-fill" />
                                          <i className="bi bi-star-half" />
                                        </small>
                                        <span>
                                          <small>{product.rating}</small>
                                        </span>
                                      </div>
                                    </div>
                                    <div className="d-grid mt-2">
                                      <Link to="#!" className="btn btn-primary">
                                        <svg
                                          xmlns="http://www.w3.org/2000/svg"
                                          width={16}
                                          height={16}
                                          viewBox="0 0 24 24"
                                          fill="none"
                                          stroke="currentColor"
                                          strokeWidth={2}
                                          strokeLinecap="round"
                                          strokeLinejoin="round"
                                          className="feather feather-plus"
                                        >
                                          <line
                                            x1={12}
                                            y1={5}
                                            x2={12}
                                            y2={19}
                                          />
                                          <line
                                            x1={5}
                                            y1={12}
                                            x2={19}
                                            y2={12}
                                          />
                                        </svg>
                                        Add to cart
                                      </Link>
                                    </div>
                                    <div className="d-flex justify-content-start text-center mt-3">
                                      <div
                                        className="deals-countdown w-100"
                                        data-countdown="2022/11/11 00:00:00"
                                      />
                                    </div>
                                  </div>
                                </div>
                              </div>
                            </div>
                          ))}
                        </Slider>
                      </div>
                    </div>
                  </div>
                </div>
              </section>
            </>
            <>
              {/* cta section */}
              <section>
                <div
                  className="container"
                  style={{
                    background: `url(${map})no-repeat`,
                    backgroundSize: "cover",
                  }}
                >
                  {/* <hr className="my-lg-14 my-8"> */}
                  {/* row */}
                  <div className="row align-items-center text-center justify-content-center">
                    <div className=" col-lg-6 col-md-6 fade-in-left">
                      <div className="mb-6">
                        <div className="mb-7">
                          {/* heading */}
                          <h1>Get the Ecocart app</h1>
                          <h5 className="mb-0">
                            Get the Best Eco-Friendly Shopping Experience!
                          </h5>
                        </div>
                        <div>
                          {/* app */}
                          {/* <small>Download app from</small> */}
                          <ul className="list-inline mb-0 mt-2 ">
                            {/* list item */}
                            <li className="list-inline-item">
                              {/* img */}
                              <Link to="#!">
                                {" "}
                                <img
                                  src={appstore}
                                  alt="appstore"
                                  style={{ width: 140 }}
                                />
                              </Link>
                            </li>
                            <li className="list-inline-item">
                              {/* img */}
                              <Link to="#!">
                                {" "}
                                <img
                                  src={googleplay}
                                  alt="googleplay"
                                  style={{ width: 140 }}
                                />
                              </Link>
                            </li>
                          </ul>
                        </div>
                      </div>
                    </div>
                    <div className=" offset-lg-2 col-lg-4 col-md-6 fade-zoom">
                      <div className="text-lg-start">
                        {/* img */}
                        <img src={phone} alt="iphone" className=" img-fluid" />
                      </div>
                    </div>
                  </div>
                  {/* <hr className="my-lg-14 my-8"> */}
                </div>
              </section>
            </>
          </>
        )}
      </div>
    </div>
  );
};

export default Home;
